plugins {
    // Apply the org.jetbrains.kotlin.jvm Plugin to add support for Kotlin.
    id("org.jetbrains.kotlin.jvm") version "1.6.21"

    id("com.github.johnrengelman.shadow") version "7.1.2"

    // Apply the java-library plugin for API and implementation separation.
    `java-library`
}

tasks {
    shadowJar {
        archiveBaseName.set("jsurferShim")
        archiveClassifier.set("")
        archiveVersion.set("")
        destinationDirectory.set(File(projectDir, ""))
    }
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    implementation(group = "com.github.jsurfer", name = "jsurfer-fastjson", version = "1.6.3")

    // Align versions of all Kotlin components
    implementation(platform("org.jetbrains.kotlin:kotlin-bom"))

    // Use the Kotlin JDK 8 standard library.
    implementation("org.jetbrains.kotlin:kotlin-stdlib-jdk8")

    // This dependency is used internally, and not exposed to consumers on their own compile
    // classpath.
    implementation("com.google.guava:guava:31.0.1-jre")

    // This dependency is exported to consumers, that is to say found on their compile classpath.
    api("org.apache.commons:commons-math3:3.6.1")
}
